import * as types from "../../actionTypes";
import ItemTypeApi from "../../../api/ItemTypeApi";

let actionsFunction = {

  //CRUD METHODS

  // Create itemtype
  createItemType: function(itemtype) {
    return function(dispatch) {
      return ItemTypeApi
        .createItemType(itemtype)
        .then(itemtype => {
          dispatch(actionsFunction.createItemTypeSuccess(itemtype));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createItemTypeSuccess: function(itemtype) {
    return { type: types.CREATE_ITEMTYPE_SUCCESS, payload: itemtype };
  },


  // Delete itemtype
  deleteItemType: function(id) {
    return function(dispatch) {
      return ItemTypeApi
        .deleteItemType(id)
        .then(itemtype => {
          dispatch(actionsFunction.deleteItemTypeSuccess(itemtype));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteItemTypeSuccess: function(itemtype) {
    return { type: types.DELETE_ITEMTYPE_SUCCESS, payload: itemtype };
  },


  // Find by Name
  findByName: function(key) {
    return function(dispatch) {
      return ItemTypeApi
        .findByName(key)
        .then(item => {
          dispatch(actionsFunction.findByNameSuccess(item));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  findByNameSuccess: function(item) {
    return { type: types.FINDBYNAME_ITEMTYPE_SUCCESS, payload: item };
  },


  // Get itemtype
  loadItemType: function(id) {
    return function(dispatch) {
      return ItemTypeApi
        .getOneItemType(id)
        .then(itemtype => {
          dispatch(actionsFunction.loadItemTypeSuccess(itemtype));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadItemTypeSuccess: function(itemtype) {
    return { type: types.GET_ITEMTYPE_SUCCESS, payload: itemtype };
  },

  // Load  list
  loadItemTypeList: function() {
    return function(dispatch) {
      return ItemTypeApi
        .getItemTypeList()
        .then(list => {
          dispatch(actionsFunction.loadItemTypeListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadItemTypeListSuccess: function(list) {
    return { type: types.LIST_ITEMTYPE_SUCCESS, payload: list };
  },

	
  // Save itemtype
  saveItemType: function(itemtype) {
    return function(dispatch) {
      return ItemTypeApi
        .saveItemType(itemtype)
        .then(itemtype => {
          dispatch(actionsFunction.saveItemTypeSuccess(itemtype));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveItemTypeSuccess: function(itemtype) {
    return { type: types.UPDATE_ITEMTYPE_SUCCESS, payload: itemtype };
  },


};

export default actionsFunction;
